// src/sections/WelcomeSection.js
import React from 'react';
import './WelcomeSection.module.css'; // Make sure this line exists and is correct

const WelcomeSection = ({ onRsvpClick, onScrollToDetails }) => {
  return (
    <section className="welcome-section">
        <div className="welcome-heading">
            <h1>The Bowens</h1>
            <p>July 17, 2027 — Coles Garden, Oklahoma City</p>
        </div>
        {/* The button class 'rsvp-button-outline' should be defined globally in index.css */}
        <button className="rsvp-button-outline" onClick={onRsvpClick}>RSVP</button>
        <div className="scroll-indicator" onClick={onScrollToDetails}>↓</div>
    </section>
  );
};

export default WelcomeSection;
