// src/sections/WelcomeSection.js
import React from 'react';
import styles from './WelcomeSection.module.css'; // Correct way to import CSS Modules

const WelcomeSection = ({ onRsvpClick, onScrollToDetails }) => {
  return (
    <section className={styles['welcome-section']}> {/* Apply class using styles object */}
        <div className={styles['welcome-heading']}> {/* Apply class using styles object */}
            <h1>The Bowens</h1>
            <p>July 17, 2027 — Coles Garden, Oklahoma City</p>
        </div>
        {/* rsvp-button-outline is a global class, so it's fine as is if it exists in index.css */}
        <button className="rsvp-button-outline" onClick={onRsvpClick}>RSVP</button>
        <div className={styles['scroll-indicator']} onClick={onScrollToDetails}>↓</div> {/* Apply class using styles object */}
    </section>
  );
};

export default WelcomeSection;
