// src/modals/PhotoUploadModal/PhotoUploadModal.js
import React, { useState } from 'react';
import styles from './PhotoUploadModal.module.css';

const PhotoUploadModal = ({ isOpen, onClose, onUploadSuccess }) => {
    const [selectedFiles, setSelectedFiles] = useState([]); // Array to hold multiple files
    const [uploadStates, setUploadStates] = useState({}); // Stores progress, error, success for each file
    const [isUploadingAll, setIsUploadingAll] = useState(false); // Track overall upload process
    const [overallError, setOverallError] = useState(''); // General errors (e.g., if no files)
    const [isSubmitted, setIsSubmitted] = useState(false); // NEW: State to track if submission is complete

    const CLOUDINARY_CLOUD_NAME = 'dtmhmxqiw'; // Your Cloud Name
    const CLOUDINARY_UPLOAD_PRESET = 'wedding_upload_preset'; // Your Unsigned Upload Preset Name

    const addFiles = (newFiles) => {
        const validFiles = Array.from(newFiles).filter(file => file.type.startsWith('image/'));
        setSelectedFiles(prevFiles => {
            const updatedFiles = [...prevFiles];
            validFiles.forEach(newFile => {
                if (!updatedFiles.some(existingFile => existingFile.name === newFile.name && existingFile.size === newFile.size)) {
                    updatedFiles.push(newFile);
                }
            });
            return updatedFiles;
        });

        setUploadStates(prevStates => {
            const newUploadStates = { ...prevStates };
            validFiles.forEach(file => {
                if (!newUploadStates[file.name]) {
                    newUploadStates[file.name] = {
                        progress: 0,
                        error: '',
                        success: false,
                        url: '',
                        previewUrl: URL.createObjectURL(file)
                    };
                }
            });
            return newUploadStates;
        });
        setOverallError('');
        setIsSubmitted(false); // Reset submitted state if new files are added
    };

    const handleFileChange = (event) => {
        addFiles(event.target.files);
        event.target.value = null; // Clear input so same file can be selected again
    };

    const handleDrop = (event) => {
        event.preventDefault();
        event.currentTarget.classList.remove(styles['drag-over']);
        addFiles(event.dataTransfer.files);
    };

    const handleDragOver = (event) => {
        event.preventDefault();
        event.currentTarget.classList.add(styles['drag-over']);
    };

    const handleDragLeave = (event) => {
        event.currentTarget.classList.remove(styles['drag-over']);
    };

    const removeFile = (fileName) => {
        setSelectedFiles(prevFiles => {
            const updatedFiles = prevFiles.filter(file => file.name !== fileName);
            // If all files are removed after successful upload, reset isSubmitted
            if (updatedFiles.length === 0 && uploadStates[fileName]?.success) {
                setIsSubmitted(false);
            }
            return updatedFiles;
        });
        setUploadStates(prevStates => {
            const newStates = { ...prevStates };
            delete newStates[fileName];
            return newStates;
        });
        setOverallError('');
    };

    const handleUploadAll = async () => {
        if (selectedFiles.length === 0) {
            setOverallError('Please select at least one file to upload.');
            return;
        }

        setIsUploadingAll(true);
        setOverallError('');
        const uploadedUrls = [];
        let hasError = false;

        for (const file of selectedFiles) {
            // Skip files that were already successfully uploaded
            if (uploadStates[file.name]?.success) {
                uploadedUrls.push(uploadStates[file.name].url);
                continue;
            }

            setUploadStates(prev => ({
                ...prev,
                [file.name]: { ...prev[file.name], progress: 1, error: '', success: false }
            }));

            const reader = new FileReader();
            reader.readAsDataURL(file);

            await new Promise(resolve => {
                reader.onloadend = async () => {
                    const base64data = reader.result;

                    try {
                        const response = await fetch('/.netlify/functions/upload-image', {
                            method: 'POST',
                            headers: { 'Content-Type': 'application/json' },
                            body: JSON.stringify({ file: base64data }),
                        });

                        if (!response.ok) {
                            const errorData = await response.json();
                            throw new Error(errorData.message || 'Upload failed.');
                        }

                        const data = await response.json();
                        uploadedUrls.push(data.secure_url);
                        setUploadStates(prev => ({ ...prev, [file.name]: { ...prev[file.name], progress: 100, success: true, url: data.secure_url } }));

                    } catch (error) {
                        console.error(`Error uploading ${file.name}:`, error);
                        setUploadStates(prev => ({ ...prev, [file.name]: { ...prev[file.name], error: error.message || 'Failed', success: false } }));
                        hasError = true;
                    } finally {
                        resolve();
                    }
                };
                reader.onerror = () => {
                    setUploadStates(prev => ({ ...prev, [file.name]: { ...prev[file.name], error: 'Failed to read file.', success: false } }));
                    hasError = true;
                    resolve();
                };
            });
        }

        setIsUploadingAll(false);
        if (!hasError) {
            onUploadSuccess(uploadedUrls); // Pass array of URLs
            setIsSubmitted(true); // NEW: Mark as submitted
            // Don't clear selectedFiles or uploadStates here so previews remain
            // Don't close modal here, let user click "Done" (now "Submit")
        } else {
            setOverallError('Some files failed to upload. Please check errors above.');
        }
    };

    const handleCloseModal = () => { // Renamed handleDone to handleCloseModal for clarity
        // Clear all states on close to reset the modal for next time
        setSelectedFiles([]);
        setUploadStates({});
        setIsSubmitted(false);
        setOverallError('');
        onClose();
    };


    if (!isOpen) return null;

    const hasFilesSelected = selectedFiles.length > 0;

    return (
        <div className={`simple-modal ${isOpen ? 'show' : ''}`} onClick={handleCloseModal} role="dialog" aria-modal="true">
            <div className={`simple-modal-content ${styles['photo-upload-modal-content']}`} onClick={e => e.stopPropagation()}>
                {/* Close Button (X icon) */}
                <button className={styles['close-button']} onClick={handleCloseModal} disabled={isUploadingAll}>
                    <svg xmlns="http://www.w3.org/2000/svg" viewBox="0 0 24 24" fill="currentColor" className="w-6 h-6">
                        <path fillRule="evenodd" d="M5.47 5.47a.75.75 0 011.06 0L12 10.94l5.47-5.47a.75.75 0 111.06 1.06L13.06 12l5.47 5.47a.75.75 0 11-1.06 1.06L12 13.06l-5.47 5.47a.75.75 0 01-1.06-1.06L10.94 12 5.47 6.53a.75.75 0 010-1.06z" clipRule="evenodd" />
                    </svg>
                </button>

                {/* Title and Subtitle */}
                <h2 className={styles['modal-title']}>Upload and attach files</h2>
                <p className={styles['modal-subtitle']}>Upload and attach files to this project.</p>

                {/* Upload Box / Drag-and-Drop Area */}
                <div
                    className={`${styles['upload-box']} ${styles['drag-drop-box']} ${overallError ? styles['has-error'] : ''}`} // Add has-error class
                    onDrop={handleDrop}
                    onDragOver={handleDragOver}
                    onDragLeave={handleDragLeave}
                >
                    <svg xmlns="http://www.w3.org/2000/svg" viewBox="0 0 24 24" fill="currentColor" className={styles['cloud-upload-icon']}>
                        <path fillRule="evenodd" d="M11.47 2.47a.75.75 0 011.06 0l3.75 3.75a.75.75 0 01-1.06 1.06L12.75 4.81V16.5a.75.75 0 01-1.5 0V4.81L8.78 7.28a.75.75 0 01-1.06-1.06l3.75-3.75zM2.25 10.5a.75.75 0 01.75-.75h1.5a.75.75 0 010 1.5H3a.75.75 0 01-.75-.75zM16.5 9.75a.75.75 0 000 1.5h1.5a.75.75 0 000-1.5h-1.5zM2.25 14.25a.75.75 0 01.75-.75h1.5a.75.75 0 010 1.5H3a.75.75 0 01-.75-.75zM16.5 13.5a.75.75 0 000 1.5h1.5a.75.75 0 000-1.5h-1.5zM6.095 19.957a.75.75 0 01.374-.608l2.5-1.75a.75.75 0 01.936.883l-1.454 2.057l1.722 1.289a.75.75 0 01-.608 1.258l-2.5-1.75a.75.75 0 01-.374-.608V19.957zM14.205 19.957a.75.75 0 01.374-.608l2.5-1.75a.75.75 0 01.936.883l-1.454 2.057l1.722 1.289a.75.75 0 01-.608 1.258l-2.5-1.75a.75.75 0 01-.374-.608V19.957zM2.25 18a.75.75 0 01.75-.75h1.5a.75.75 0 010 1.5H3a.75.75 0 01-.75-.75zM16.5 17.25a.75.75 0 000 1.5h1.5a.75.75 0 000-1.5h-1.5z" clipRule="evenodd" />
                    </svg>
                    <label htmlFor="photo-upload-input" className={styles['upload-text-prompt']}>
                        Click to upload or drag and drop
                    </label>
                    <span className={styles['upload-hint']}>SVG, PNG, JPG or GIF (max. 800x400px)</span>
                    <input
                        type="file"
                        accept="image/svg+xml,image/png,image/jpeg,image/gif"
                        onChange={handleFileChange}
                        className={styles['file-input']}
                        id="photo-upload-input"
                        multiple // Allow multiple file selection
                    />
                </div>

                {hasFilesSelected && (
                    <div className={styles['file-list-container']}>
                        {selectedFiles.map((file) => (
                            <div key={file.name} className={styles['file-preview-item']}>
                                <div className={styles['file-thumbnail']}>
                                    {uploadStates[file.name]?.previewUrl && <img src={uploadStates[file.name].previewUrl} alt="Selected preview" className={styles['thumbnail-image']} />}
                                </div>
                                <div className={styles['file-details']}>
                                    <p className={styles['file-name']}>{file.name}</p>
                                    <p className={styles['file-size']}>{Math.round(file.size / 1024)} KB</p>

                                    {/* Progress bar and status for individual file */}
                                    {isUploadingAll && !uploadStates[file.name]?.error && !uploadStates[file.name]?.success && (
                                        <div className={styles['progress-wrapper']}>
                                            <div className={styles['progress-bar']} style={{ width: `${uploadStates[file.name]?.progress}%` }}></div>
                                        </div>
                                    )}
                                    {uploadStates[file.name]?.success && <span className={styles['upload-status']}><svg viewBox="0 0 24 24" fill="currentColor" className={styles['success-icon']}><path fillRule="evenodd" d="M19.916 4.626a.75.75 0 01.208 1.04l-9 13.5a.75.75 0 01-1.154.114l-3-3a.75.75 0 011.06-1.06l2.769 2.769 8.792-13.188a.75.75 0 011.04-.208z" clipRule="evenodd" /></svg> Uploaded</span>}
                                    {uploadStates[file.name]?.error && <p className={styles['error-message']}>{uploadStates[file.name].error}</p>}
                                </div>
                                {!isUploadingAll && (
                                    <button className={styles['remove-file-button']} onClick={() => removeFile(file.name)}>
                                        <svg xmlns="http://www.w3.org/2000/svg" viewBox="0 0 24 24" fill="currentColor" className="w-6 h-6">
                                            <path fillRule="evenodd" d="M16.5 4.478l.271.272a.75.75 0 010 1.06L13.06 9.75l3.71 3.71a.75.75 0 01-1.06 1.06l-3.71-3.71l-3.71 3.71a.75.75 0 01-1.06-1.06l3.71-3.71l-3.71-3.71a.75.75 0 111.06-1.06l3.71 3.71l3.71-3.71a.75.75 0 011.06 0z" clipRule="evenodd" />
                                        </svg>
                                    </button>
                                )}
                            </div>
                        ))}
                    </div>
                )}

                {overallError && <p className={styles['overall-error-message']}>{overallError}</p>}

                {/* Main Action Buttons */}
                <div className={styles['button-row']}>
                    {isSubmitted ? (
                        <p className={styles['submission-success-message']}>
                            All photos uploaded to the <a href="#" onClick={handleCloseModal} className={styles['gallery-link']}>gallery</a>!
                        </p>
                    ) : (
                        <button
                            className={`button ${styles['upload-button']}`}
                            onClick={handleUploadAll}
                            disabled={selectedFiles.length === 0 || isUploadingAll}
                        >
                            {isUploadingAll ? 'Uploading...' : 'Submit'}
                        </button>
                    )}
                </div>
            </div>
        </div>
    );
};

export default PhotoUploadModal;
