import React from 'react';

// Placeholder images for the gallery - you can replace these with your own
const galleryImages = [
  'https://images.unsplash.com/photo-1511795409834-ef04bbd51622?q=80&w=2070&auto=format&fit=crop',
  'https://images.unsplash.com/photo-1515934751635-c81c6bc9a2d8?q=80&w=2070&auto=format&fit=crop',
  'https://images.unsplash.com/photo-1545041650-131b39245904?q=80&w=2070&auto=format&fit=crop',
  'https://images.unsplash.com/photo-1519225421980-715cb0295b73?q=80&w=2070&auto=format&fit=crop',
  'https://images.unsplash.com/photo-1520854221256-17451cc331bf?q=80&w=2070&auto=format&fit=crop',
  'https://images.unsplash.com/photo-1511285560921-15b5a9305442?q=80&w=2070&auto=format&fit=crop',
];

const PhotoGalleryModal = ({ isOpen, onClose }) => {
    if (!isOpen) return null;

    return (
        <div className="rsvp-modal show" onClick={onClose}>
            <div className="rsvp-modal-content photo-gallery-content" onClick={e => e.stopPropagation()}>
                <div className="rsvp-modal-header">
                    <h2>Our Gallery</h2>
                    <p>Thank you for sharing our special moments!</p>
                </div>
                <div className="gallery-grid">
                    {galleryImages.map((src, index) => (
                        <div key={index} className="gallery-item">
                            <img src={src} alt={`Wedding gallery image ${index + 1}`} />
                        </div>
                    ))}
                </div>
                 <button className="button" onClick={onClose}>Close</button>
            </div>
        </div>
    );
};

export default PhotoGalleryModal;
