// src/pages/OverUnderGamePage/OverUnderGamePage.js
import React, { useState } from 'react';
import styles from './OverUnderGamePage.module.css'; // Specific styles for this page

const OverUnderGamePage = ({ onBack }) => {
  const [formData, setFormData] = useState({
    answer1: '', // Example field
    // Add more fields as needed for your game
  });

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData(prev => ({ ...prev, [name]: value }));
  };

  const encode = (data) => {
    return Object.keys(data)
      .map(key => encodeURIComponent(key) + "=" + encodeURIComponent(data[key]))
      .join("&");
  };

  const handleSubmit = async (e) => {
    e.preventDefault(); // Prevent default form submission

    // Basic validation
    if (!formData.answer1) { // Example validation
      alert('Please provide an answer for Over/Under!');
      return;
    }

    try {
      // Netlify Form Submission
      const response = await fetch("/", {
        method: "POST",
        headers: { "Content-Type": "application/x-www-form-urlencoded" },
        body: encode({ "form-name": "over-under-game", ...formData }) // IMPORTANT: form-name must match hidden form in public/
      });

      if (!response.ok) {
        throw new Error(`HTTP error! status: ${response.status}`);
      }

      alert('Over/Under submitted successfully!');
      console.log('Over/Under Game Submitted:', formData);
      setFormData({ answer1: '' }); // Reset form
      // Optionally, navigate back or show a success message
      onBack();

    } catch (error) {
      console.error('Submission error:', error);
      alert('There was an error submitting your game. Please try again.');
    }
  };

  return (
    <div className={styles['game-page']}>
      <div className={styles['game-header']}>
        <button onClick={onBack} className={styles['back-button']}>‚Üê Back</button>
        <h1>Over/Under Game</h1>
        <p>Make your predictions!</p>
      </div>

      {/* The actual form for Netlify to detect */}
      <form name="over-under-game" method="POST" data-netlify="true" onSubmit={handleSubmit}>
        <input type="hidden" name="form-name" value="over-under-game" />

        <div className={styles['game-content']}>
          {/* Example Game Question */}
          <label htmlFor="answer1">How many times will the groom say "awesome"?</label>
          <input type="number" name="answer1" id="answer1" value={formData.answer1} onChange={handleChange} />

          {/* Add more game-specific inputs here */}
        </div>

        <button type="submit" className={`button ${styles['submit-button']}`}>Submit Predictions</button>
      </form>
    </div>
  );
};

export default OverUnderGamePage;
