// src/pages/GalleryPage/GalleryPage.js
import React from 'react';
import styles from './GalleryPage.module.css';

// A helper function to create a Cloudinary download URL
const createDownloadUrl = (cloudinaryUrl) => {
    // Inserts 'fl_attachment/' into the URL right after '/upload/'
    return cloudinaryUrl.replace('/upload/', '/upload/fl_attachment/');
};

// Your placeholder/static images can go here
const staticGalleryImages = [];

const GalleryPage = ({ onBack, uploadedImages = [] }) => {
    return (
        <div className={styles['gallery-page']}>
            <div className={styles['gallery-header']}>
                <button onClick={onBack} className={styles['back-button']}>‚Üê Back</button>
                <h1>Our Gallery</h1>
                <p>Thank you for sharing our special moments!</p>
            </div>
            <div className={styles['gallery-grid']}>
                {/* Render static images (no download button) */}
                {staticGalleryImages.map((src, index) => (
                    <div key={`static-${index}`} className={styles['gallery-item']}>
                        <img src={src} alt={`Static gallery image ${index + 1}`} />
                    </div>
                ))}

                {/* Render uploaded images with a download button */}
                {uploadedImages.map((src, index) => (
                    <div key={`uploaded-${index}`} className={styles['gallery-item']}>
                        <img src={src} alt={`Uploaded wedding gallery image ${index + 1}`} />
                        <div className={styles['image-overlay']}>
                            <a 
                                href={createDownloadUrl(src)} 
                                download 
                                className={styles['overlay-button']}
                                aria-label="Download image"
                                // Stop click from bubbling up if needed
                                onClick={(e) => e.stopPropagation()} 
                            >
                                <svg xmlns="http://www.w3.org/2000/svg" viewBox="0 0 20 20" fill="currentColor">
                                  <path d="M10.75 2.75a.75.75 0 00-1.5 0v8.614L6.295 8.235a.75.75 0 10-1.09 1.03l4.25 4.5a.75.75 0 001.09 0l4.25-4.5a.75.75 0 00-1.09-1.03l-2.955 3.129V2.75z" />
                                  <path d="M3.5 12.75a.75.75 0 00-1.5 0v2.5A2.75 2.75 0 004.75 18h10.5A2.75 2.75 0 0018 15.25v-2.5a.75.75 0 00-1.5 0v2.5c0 .69-.56 1.25-1.25 1.25H4.75c-.69 0-1.25-.56-1.25-1.25v-2.5z" />
                                </svg>
                            </a>
                        </div>
                    </div>
                ))}
            </div>
        </div>
    );
};

export default GalleryPage;
