import React, { useRef, useEffect } from 'react'; // Add useEffect
// ... all other section imports

// Accept the new props: targetSection and onScrollComplete
const HomePage = ({ onNavigate, onRsvpClick, onUploadClick, targetSection, onScrollComplete }) => {
    const sectionRefs = {
        schedule: useRef(null),
        menu: useRef(null),
        hotels: useRef(null),
        photos: useRef(null), // Make sure you have a ref for every section ID
        gallery: useRef(null),
        games: useRef(null),
        spotify: useRef(null),
        registry: useRef(null),
        directions: useRef(null),
    };

    // This is the new logic that handles the scroll
    useEffect(() => {
        if (targetSection && sectionRefs[targetSection]?.current) {
            sectionRefs[targetSection].current.scrollIntoView({ 
                behavior: 'smooth', 
                block: 'start' 
            });
            // Tell the parent App component that the scroll is done
            onScrollComplete();
        }
    }, [targetSection, onScrollComplete, sectionRefs]);


    // The old scrollToSection function is no longer needed here
    // ...

    // The return JSX needs to have the refs assigned to the correct sections
    return (
        <div className="content-wrapper">
            <WelcomeSection onRsvpClick={onRsvpClick} onScrollToDetails={() => handleNavigate('schedule')} />
            <ScheduleSection sectionRef={sectionRefs.schedule} />
            <MenuSection sectionRef={sectionRefs.menu} />
            {/* Make sure each section component that can be scrolled to has its ref */}
            <PhotoUploadSection sectionRef={sectionRefs.photos} onUploadButtonClick={onUploadClick} />
            <GalleryLinkSection sectionRef={sectionRefs.gallery} onViewGallery={() => onNavigate('gallery')} />
            <HotelsSection sectionRef={sectionRefs.hotels} />
            <GamesSection
                sectionRef={sectionRefs.games}
                onOverUnderClick={() => onNavigate('over-under')}
                onBingoClick={() => onNavigate('bingo')}
            />
            <SpotifySection sectionRef={sectionRefs.spotify} />
            <RegistrySection sectionRef={sectionRefs.registry} />
            <DirectionsSection sectionRef={sectionRefs.directions} />

            {/* ... footer ... */}
        </div>
    );
};

export default HomePage;
