import React, { useRef, useEffect } from 'react';

// CORRECTED: The path now goes up two directories to find the 'sections' folder
import WelcomeSection from '../../sections/WelcomeSection/WelcomeSection';
import ScheduleSection from '../../sections/ScheduleSection/ScheduleSection';
import MenuSection from '../../sections/MenuSection/MenuSection';
import PhotoUploadSection from '../../sections/PhotoUploadSection/PhotoUploadSection';
import GalleryLinkSection from '../../sections/GalleryLinkSection/GalleryLinkSection';
import HotelsSection from '../../sections/HotelsSection/HotelsSection';
import GamesSection from '../../sections/GamesSection/GamesSection';
import SpotifySection from '../../sections/SpotifySection/SpotifySection';
import RegistrySection from '../../sections/RegistrySection/RegistrySection';
import DirectionsSection from '../../sections/DirectionsSection/DirectionsSection';

const HomePage = ({ onNavigate, onRsvpClick, onUploadClick, targetSection, onScrollComplete }) => {
    // This ref object holds references to each section's DOM element
    const sectionRefs = {
        schedule: useRef(null),
        menu: useRef(null),
        photos: useRef(null),
        gallery: useRef(null),
        games: useRef(null),
        spotify: useRef(null),
        registry: useRef(null),
        directions: useRef(null),
    };

    // This effect runs when the targetSection prop changes, triggering the scroll
    useEffect(() => {
        if (targetSection && sectionRefs[targetSection]?.current) {
            sectionRefs[targetSection].current.scrollIntoView({ 
                behavior: 'smooth', 
                block: 'start' 
            });
            onScrollComplete();
        }
    }, [targetSection, onScrollComplete, sectionRefs]);

    // This is the JSX that renders the full home page
    return (
        <div className="content-wrapper">
            <WelcomeSection onRsvpClick={onRsvpClick} onScrollToDetails={() => onNavigate('schedule')} />
            <ScheduleSection sectionRef={sectionRefs.schedule} />
            <MenuSection sectionRef={sectionRefs.menu} />
            <PhotoUploadSection sectionRef={sectionRefs.photos} onUploadButtonClick={onUploadClick} />
            <GalleryLinkSection sectionRef={sectionRefs.gallery} onViewGallery={() => onNavigate('gallery')} />
            <HotelsSection sectionRef={sectionRefs.hotels} />
            <GamesSection
                sectionRef={sectionRefs.games}
                onOverUnderClick={() => onNavigate('over-under')}
                onBingoClick={() => onNavigate('bingo')}
            />
            <SpotifySection sectionRef={sectionRefs.spotify} />
            <RegistrySection sectionRef={sectionRefs.registry} />
            <DirectionsSection sectionRef={sectionRefs.directions} />

            <footer style={{textAlign: 'center', marginTop: '4rem', color: '#777'}}>
                <p>Â© 2027 The Bowen's. All Rights Reserved.</p>
                <p>With love, from the future Mr. & Mrs. Bowen</p>
            </footer>
        </div>
    );
};

export default HomePage;
