// src/sections/PhotoUploadSection.js
import React from 'react';
import './PhotoUploadSection.module.css'; // Import its specific CSS

const PhotoUploadSection = ({ sectionRef }) => { // ADDED sectionRef prop
  return (
    <section id="photo-upload" ref={sectionRef} className="content-section"> {/* ADDED ref={sectionRef} */}
      <h2>Photo Upload</h2>
      <p>Scan the QR codes at your table or click below to upload your photos from our special day!</p>
      <button className="button">Upload Photos</button>
    </section>
  );
};

export default PhotoUploadSection;
