import React, { useState, useEffect, useRef } from 'react';

// --- IMPORT YOUR VENUE IMAGES ---
// Make sure these images are in an 'assets' folder inside your 'src' folder.
import royalGarden from './assets/WEDDINGS-THE-ROYAL-GARDEN-10.webp';
import waterfall2 from './assets/WEDDINGS-Waterfall-02.webp';
import waterfall3 from './assets/WEDDINGS-Waterfall-03.webp';
import waterfall6 from './assets/WEDDINGS-Waterfall-06.webp';


// --- CSS STYLES ---
const GlobalStyles = () => (
  <style>{`
    @import url('https://fonts.googleapis.com/css2?family=Cormorant+Garamond:wght@300;400;500;600;700&family=Montserrat:wght@300;400;500;600;700&display=swap');

    :root {
      --color-white: #FFFFFF;
      --color-black: #000000;
      --color-mocha: #6F4E37;
      --color-light-grey: #F8F8F8;
      --color-dark-grey: #333333;
      --font-heading: 'Cormorant Garamond', serif;
      --font-body: 'Montserrat', sans-serif;
    }

    body {
      margin: 0;
      font-family: var(--font-body);
      background-color: var(--color-white);
      color: var(--color-dark-grey);
      line-height: 1.7;
      -webkit-font-smoothing: antialiased;
      -moz-osx-font-smoothing: grayscale;
    }

    .app-container {
      display: flex;
      height: 100vh;
      overflow: hidden;
    }

    /* --- Left Column: Image Slideshow --- */
    .image-column {
      width: 60%; /* Adjusted width */
      height: 100vh;
      position: relative;
      overflow: hidden;
      background-color: var(--color-black);
    }

    .slideshow-image {
      position: absolute;
      top: 0;
      left: 0;
      width: 100%;
      height: 100%;
      background-size: cover;
      background-position: center;
      opacity: 0;
      transition: opacity 1.5s ease-in-out;
      filter: brightness(0.8);
      z-index: 1;
    }

    .slideshow-image.active {
      opacity: 1;
      z-index: 2;
    }

    .slideshow-overlay {
      position: absolute;
      bottom: 40px;
      left: 50%;
      transform: translateX(-50%);
      z-index: 3;
      color: var(--color-white);
      text-shadow: 1px 1px 3px rgba(0,0,0,0.5);
      text-align: center;
      width: 100%;
    }

    .slideshow-overlay h1 {
      font-family: var(--font-heading);
      font-size: 4rem;
      margin: 0 0 0.5rem 0;
      font-weight: 600;
      color: var(--color-white);
    }

    .slideshow-overlay p {
      font-size: 1.2rem;
      margin: 0;
      color: var(--color-white);
    }

    /* --- Right Column: Content --- */
    .content-column {
      width: 40%; /* Adjusted width */
      height: 100vh;
      overflow-y: auto;
      scroll-behavior: smooth;
    }

    .content-wrapper {
      max-width: 700px;
      margin: 0 auto;
      padding: 0 3rem;
    }

    .welcome-section {
        height: 100vh;
        display: flex;
        flex-direction: column;
        justify-content: center;
        align-items: center;
        text-align: center;
    }

    .welcome-section h2 {
        font-family: var(--font-heading);
        font-size: 1.5rem;
        font-weight: 400;
        letter-spacing: 1px;
        margin: 0 0 1.5rem 0;
    }

    .rsvp-button-outline {
        background-color: transparent;
        color: var(--color-mocha);
        border: 1px solid var(--color-mocha);
        padding: 0.7rem 2.5rem;
        font-size: 1rem;
        font-family: var(--font-body);
        cursor: pointer;
        border-radius: 5px;
        transition: all 0.3s ease;
    }

    .rsvp-button-outline:hover {
        background-color: var(--color-mocha);
        color: var(--color-white);
    }

    .scroll-indicator {
        margin-top: 1.5rem;
        font-size: 1.5rem;
        color: var(--color-mocha);
        animation: bounce 2s infinite;
        cursor: pointer;
    }

    @keyframes bounce {
      0%, 20%, 50%, 80%, 100% {
        transform: translateY(0);
      }
      40% {
        transform: translateY(-10px);
      }
      60% {
        transform: translateY(-5px);
      }
    }

    .content-section {
      padding: 4rem 0;
      border-bottom: 1px solid #eee;
    }

    .content-section:last-of-type {
        border-bottom: none;
    }

    .content-section h2 {
      font-family: var(--font-heading);
      font-size: 2.5rem;
      font-weight: 500;
      margin-top: 0;
      margin-bottom: 1.5rem;
      color: var(--color-mocha);
    }

    .content-section p {
        font-size: 1.1rem;
        margin-bottom: 1rem;
    }

    .content-section a {
      color: var(--color-mocha);
      text-decoration: none;
      transition: color 0.3s ease;
      font-weight: 500;
    }

    .content-section a:hover {
      color: var(--color-black);
      text-decoration: underline;
    }

    /* --- Navigation Menu --- */
    .menu-button {
      position: fixed;
      top: 25px;
      left: 25px;
      z-index: 1001;
      background-color: rgba(255, 255, 255, 0.9);
      color: var(--color-dark-grey);
      border: 1px solid rgba(0, 0, 0, 0.1);
      padding: 0.5rem;
      font-size: 1rem;
      cursor: pointer;
      border-radius: 5px;
      transition: all 0.3s ease;
      width: 50px;
      height: 40px;
      display: flex;
      flex-direction: column;
      justify-content: space-around;
      align-items: center;
    }

    .menu-button:hover {
      background-color: var(--color-white);
    }

    .menu-button span {
        display: block;
        width: 22px;
        height: 2px;
        background-color: var(--color-dark-grey);
    }

    .nav-sidebar {
      position: fixed;
      top: 0;
      left: -300px;
      width: 280px;
      height: 100%;
      background-color: var(--color-white);
      box-shadow: 2px 0 15px rgba(0,0,0,0.1);
      transition: left 0.4s ease-in-out;
      z-index: 1000;
      padding-top: 80px;
    }

    .nav-sidebar.open {
      left: 0;
    }

    .nav-sidebar ul {
      list-style: none;
      padding: 0;
      margin: 0;
    }

    .nav-sidebar li a {
      display: block;
      padding: 1rem 2rem;
      color: var(--color-dark-grey);
      text-decoration: none;
      font-size: 1.2rem;
      font-family: var(--font-heading);
      transition: background-color 0.3s, color 0.3s;
    }

    .nav-sidebar li a:hover {
      background-color: var(--color-light-grey);
      color: var(--color-black);
    }

    .nav-overlay {
        position: fixed;
        top: 0;
        left: 0;
        width: 100%;
        height: 100%;
        background-color: rgba(0,0,0,0.4);
        opacity: 0;
        visibility: hidden;
        transition: opacity 0.4s ease-in-out, visibility 0.4s;
        z-index: 999;
    }

    .nav-overlay.open {
        opacity: 1;
        visibility: visible;
    }

    /* --- RSVP Modal Styles --- */
    .rsvp-modal {
      position: fixed; 
      top: 0; 
      left: 0; 
      right: 0; 
      bottom: 0;
      background-color: rgba(0,0,0,0.6); 
      display: flex;
      align-items: center; 
      justify-content: center; 
      z-index: 2000;
      opacity: 0;
      visibility: hidden;
      transition: opacity 0.3s ease, visibility 0.3s;
    }

    .rsvp-modal.show {
      opacity: 1;
      visibility: visible;
    }

    .rsvp-modal-content {
      background-color: white; 
      padding: 2rem; 
      border-radius: 8px;
      box-shadow: 0 5px 15px rgba(0,0,0,0.3);
      transform: translateY(-20px);
      transition: transform 0.3s ease;
      width: 90%;
      max-width: 500px;
      text-align: center;
    }

    .rsvp-modal.show .rsvp-modal-content {
        transform: translateY(0);
    }

    .rsvp-modal-header h2 {
        font-family: var(--font-heading);
        font-size: 2rem;
        margin: 0 0 0.5rem;
    }
    .rsvp-modal-header p {
        margin: 0 0 2rem;
        color: #666;
    }

    .rsvp-form-grid {
        display: grid;
        grid-template-columns: 1fr 1fr;
        gap: 1rem;
        margin-bottom: 1rem;
    }

    .rsvp-form-field {
        display: flex;
        flex-direction: column;
        text-align: left;
    }

    .rsvp-form-field label {
        margin-bottom: 0.5rem;
        font-size: 0.9rem;
        color: #555;
    }

    .rsvp-form-field input, .rsvp-form-field textarea {
        width: 100%;
        padding: 0.8rem 1rem;
        border: 1px solid #ccc;
        border-radius: 5px;
        font-size: 1rem;
        font-family: var(--font-body);
        box-sizing: border-box;
    }

    .rsvp-form-field textarea {
        resize: vertical;
        min-height: 80px;
    }

    .rsvp-question {
        text-align: left;
        margin-bottom: 1.5rem;
    }
    .rsvp-question p {
        margin: 0 0 0.75rem;
        font-weight: 500;
    }

    .radio-group button {
        width: 100%;
        background: #f0f0f0;
        border: 1px solid #ddd;
        padding: 0.75rem;
        margin-bottom: 0.5rem;
        border-radius: 5px;
        cursor: pointer;
        transition: all 0.2s;
        text-align: left;
    }

    .radio-group button.selected {
        background: var(--color-mocha);
        color: white;
        border-color: var(--color-mocha);
    }

    .rsvp-modal .button {
        width: 100%;
        margin-top: 1.5rem;
    }

    .rsvp-modal .cancel-button {
        background: none;
        border: none;
        color: #888;
        margin-top: 1rem;
        cursor: pointer;
    }

    /* General Modal for simple messages */
    .simple-modal {
      position: fixed; 
      top: 0; 
      left: 0; 
      right: 0; 
      bottom: 0;
      background-color: rgba(0,0,0,0.5); 
      display: flex;
      align-items: center; 
      justify-content: center; 
      z-index: 2000;
      opacity: 0;
      visibility: hidden;
      transition: opacity 0.3s ease, visibility 0.3s;
    }

    .simple-modal.show {
      opacity: 1;
      visibility: visible;
    }

    .simple-modal-content {
      background-color: white; 
      padding: 2rem 3rem; 
      border-radius: 8px;
      text-align: center; 
      box-shadow: 0 5px 15px rgba(0,0,0,0.3);
      transform: translateY(-20px);
      transition: transform 0.3s ease;
    }

    .simple-modal.show .simple-modal-content {
        transform: translateY(0);
    }

    .simple-modal-content p {
      font-family: var(--font-body); 
      font-size: 1.1rem; 
      margin: 0 0 1.5rem 0;
    }


    /* --- Responsive Design --- */
    @media (max-width: 1024px) {
      .app-container {
        flex-direction: column;
      }
      .image-column {
        display: none; /* Hide image column on smaller screens */
      }
      .content-column {
        width: 100%;
      }
      .content-wrapper {
        padding: 0 1.5rem;
      }
      .menu-button {
        background-color: var(--color-mocha);
        border-color: var(--color-mocha);
      }
       .menu-button span {
        background-color: var(--color-white);
       }
    }

    @media (max-width: 768px) {
        .slideshow-overlay h1 {
            font-size: 3rem;
        }
        .content-section h2 {
            font-size: 2rem;
        }
        .rsvp-form-grid {
            grid-template-columns: 1fr;
        }
    }
  `}</style>
);

// --- Simple Message Modal Component ---
const Modal = ({ message, onClose }) => {
    if (!message) return null;
    return (
      <div className={`simple-modal ${message ? 'show' : ''}`} onClick={onClose}>
        <div className="simple-modal-content" onClick={e => e.stopPropagation()}>
          <p>{message}</p>
          <button className="button" onClick={onClose}>Close</button>
        </div>
      </div>
    );
};

// --- RSVP Modal Component ---
const RsvpModal = ({ isOpen, onClose, onSubmit }) => {
    const [step, setStep] = useState(1);
    const [formData, setFormData] = useState({
        firstName: '', lastName: '', email: '', phone: '',
        attending: null, entree: '', accommodations: null,
        address: '', message: ''
    });

    const handleInputChange = (e) => {
        const { name, value } = e.target;
        setFormData(prev => ({ ...prev, [name]: value }));
    };

    const handleOptionSelect = (name, value) => {
        setFormData(prev => ({ ...prev, [name]: value }));
    };

    const nextStep = () => setStep(prev => prev + 1);

    const handleSubmit = () => {
        console.log("RSVP Submitted:", formData);
        onSubmit(`Thank you, ${formData.firstName}! Your RSVP has been recorded.`);
        onClose();
    };

    useEffect(() => {
        if (isOpen) {
            setStep(1);
            setFormData({
                firstName: '', lastName: '', email: '', phone: '',
                attending: null, entree: '', accommodations: null,
                address: '', message: ''
            });
        }
    }, [isOpen]);

    if (!isOpen) return null;

    return (
        <div className={`rsvp-modal ${isOpen ? 'show' : ''}`} onClick={onClose}>
            <div className="rsvp-modal-content" onClick={e => e.stopPropagation()}>
                <div className="rsvp-modal-header">
                    <h2>The Bowens</h2>
                    <p>Oklahoma City, OK, USA</p>
                </div>

                {step === 1 && (
                    <div>
                        <p>Enter your name to RSVP</p>
                        <div className="rsvp-form-grid">
                            <div className="rsvp-form-field">
                                <label htmlFor="firstName">First Name</label>
                                <input type="text" name="firstName" id="firstName" value={formData.firstName} onChange={handleInputChange} />
                            </div>
                            <div className="rsvp-form-field">
                                <label htmlFor="lastName">Last Name</label>
                                <input type="text" name="lastName" id="lastName" value={formData.lastName} onChange={handleInputChange} />
                            </div>
                        </div>
                        <button className="button" onClick={nextStep} disabled={!formData.firstName || !formData.lastName}>Next</button>
                    </div>
                )}

                {step === 2 && (
                    <div>
                        <div className="rsvp-form-field" style={{marginBottom: '1rem'}}>
                            <label htmlFor="email">Email</label>
                            <input type="email" name="email" id="email" value={formData.email} onChange={handleInputChange} />
                        </div>
                        <div className="rsvp-form-field">
                            <label htmlFor="phone">Mobile Phone</label>
                            <input type="tel" name="phone" id="phone" value={formData.phone} onChange={handleInputChange} />
                        </div>
                        <button className="button" onClick={nextStep} disabled={!formData.email}>Get Started</button>
                    </div>
                )}

                {step === 3 && (
                    <div>
                        <div className="rsvp-question">
                            <p>1. Will you be able to join us at our wedding?</p>
                            <div className="radio-group">
                                <button onClick={() => handleOptionSelect('attending', 'yes')} className={formData.attending === 'yes' ? 'selected' : ''}>Joyfully Accept</button>
                                <button onClick={() => handleOptionSelect('attending', 'no')} className={formData.attending === 'no' ? 'selected' : ''}>Regretfully Decline</button>
                            </div>
                        </div>
                         <div className="rsvp-question">
                            <p>2. What entree would you prefer at our wedding?</p>
                            <div className="radio-group">
                                <button onClick={() => handleOptionSelect('entree', 'chicken')} className={formData.entree === 'chicken' ? 'selected' : ''}>Chicken</button>
                                <button onClick={() => handleOptionSelect('entree', 'pasta')} className={formData.entree === 'pasta' ? 'selected' : ''}>Pasta (vegetarian)</button>
                                <button onClick={() => handleOptionSelect('entree', 'kids')} className={formData.entree === 'kids' ? 'selected' : ''}>Kids' meal</button>
                            </div>
                        </div>
                        <button className="button" onClick={nextStep}>Next</button>
                    </div>
                )}

                {step === 4 && (
                    <div>
                        <div className="rsvp-question">
                            <p>3. Will you need accommodations near our venue?</p>
                             <div className="radio-group">
                                <button onClick={() => handleOptionSelect('accommodations', 'yes')} className={formData.accommodations === 'yes' ? 'selected' : ''}>Yes!</button>
                                <button onClick={() => handleOptionSelect('accommodations', 'no')} className={formData.accommodations === 'no' ? 'selected' : ''}>No, thanks.</button>
                            </div>
                        </div>
                        <div className="rsvp-question">
                            <p>4. What is your mailing address?</p>
                             <div className="rsvp-form-field">
                                <textarea name="address" value={formData.address} onChange={handleInputChange} placeholder="Write your answer here." />
                            </div>
                        </div>
                         <div className="rsvp-question">
                            <p>5. Share how you know the couple, wish them well or some wisdom for the future!</p>
                             <div className="rsvp-form-field">
                                <textarea name="message" value={formData.message} onChange={handleInputChange} placeholder="Write your answer here." />
                            </div>
                        </div>
                        <button className="button" onClick={handleSubmit}>Save Response</button>
                    </div>
                )}

                <button className="cancel-button" onClick={onClose}>Cancel</button>
            </div>
        </div>
    );
};


// --- ImageSlideshow Component ---
const ImageSlideshow = () => {
  const images = [ royalGarden, waterfall2, waterfall3, waterfall6 ];
  const [currentIndex, setCurrentIndex] = useState(0);
  const [isLoaded, setIsLoaded] = useState(false);

  useEffect(() => {
    const timer = setTimeout(() => setIsLoaded(true), 10);
    const interval = setInterval(() => {
      setCurrentIndex((prevIndex) => (prevIndex + 1) % images.length);
    }, 5000);
    return () => { clearTimeout(timer); clearInterval(interval); };
  }, [images.length]);

  return (
    <div className="image-column">
      {images.map((image, index) => (
        <div
          key={index}
          className={`slideshow-image ${index === currentIndex && isLoaded ? 'active' : ''}`}
          style={{ backgroundImage: `url(${image})` }}
          aria-label="Slideshow image of Coles Garden"
        ></div>
      ))}
      <div className="slideshow-overlay">
        <h1>The Bowens</h1>
        <p>July 17, 2027 — Coles Garden, Oklahoma City</p>
      </div>
    </div>
  );
};

// --- Navigation Component ---
const Navigation = ({ onNavigate, isNavOpen, setIsNavOpen }) => {
  const sections = [
    { id: 'schedule', title: 'Schedule' },
    { id: 'menu', title: 'Menu' },
    { id: 'hotels', title: 'Hotels' },
    { id: 'photos', title: 'Photos' },
    { id: 'games', title: 'Games' },
    { id: 'spotify', title: 'Playlist' },
    { id: 'registry', title: 'Registry' },
    { id: 'directions', title: 'Directions' },
  ];

  const handleNavClick = (id) => {
    onNavigate(id);
    setIsNavOpen(false);
  };

  return (
    <>
      <button className="menu-button" onClick={() => setIsNavOpen(!isNavOpen)}>
        <span></span><span></span><span></span>
      </button>
      <div className={`nav-overlay ${isNavOpen ? 'open' : ''}`} onClick={() => setIsNavOpen(false)}></div>
      <nav className={`nav-sidebar ${isNavOpen ? 'open' : ''}`}>
        <ul>
          {sections.map((section) => (
            <li key={section.id}>
              <a href={`#${section.id}`} onClick={(e) => { e.preventDefault(); handleNavClick(section.id); }}>
                {section.title}
              </a>
            </li>
          ))}
        </ul>
      </nav>
    </>
  );
};

// --- Main WeddingSite Component ---
export default function WeddingSite() {
  const [isNavOpen, setIsNavOpen] = useState(false);
  const [isRsvpModalOpen, setIsRsvpModalOpen] = useState(false);
  const [modalMessage, setModalMessage] = useState('');

  const mainContentRef = useRef(null);
  const sectionRefs = {
    schedule: useRef(null), menu: useRef(null), hotels: useRef(null),
    photos: useRef(null), games: useRef(null), spotify: useRef(null),
    registry: useRef(null), directions: useRef(null),
  };

  const handleNavigate = (id) => {
    sectionRefs[id].current.scrollIntoView({ behavior: 'smooth', block: 'start' });
  };

  const handleViewDetails = () => {
    sectionRefs.schedule.current.scrollIntoView({ behavior: 'smooth', block: 'start' });
  };

  return (
    <>
      <GlobalStyles />
      <RsvpModal 
        isOpen={isRsvpModalOpen} 
        onClose={() => setIsRsvpModalOpen(false)}
        onSubmit={(message) => setModalMessage(message)}
      />
      <Modal message={modalMessage} onClose={() => setModalMessage('')} />
      <div className="app-container">
        <Navigation onNavigate={handleNavigate} isNavOpen={isNavOpen} setIsNavOpen={setIsNavOpen} />
        <ImageSlideshow />
        <main className="content-column" ref={mainContentRef}>
          <div className="content-wrapper">

            <section className="welcome-section">
                <h2>Oklahoma City, OK, USA</h2>
                <button className="rsvp-button-outline" onClick={() => setIsRsvpModalOpen(true)}>RSVP</button>
                <div className="scroll-indicator" onClick={handleViewDetails}>↓</div>
            </section>

            <section id="schedule" ref={sectionRefs.schedule} className="content-section">
              <h2>Schedule</h2>
              <p><strong>Ceremony:</strong> 4:30 PM</p>
              <p><strong>Reception:</strong> 6:00 PM</p>
