import React, { useState, useEffect, useRef } from 'react';
import { createRoot } from 'react-dom/client';

// --- CSS STYLES ---
// In a real-world scenario, this would be in a separate index.css file.
// For this self-contained example, it's included here.
const GlobalStyles = () => (
  <style>{`
    @import url('https://fonts.googleapis.com/css2?family=Cormorant+Garamond:wght@300;400;500;600;700&family=Montserrat:wght@300;400;500;600;700&display=swap');

    :root {
      --color-white: #FFFFFF;
      --color-black: #000000;
      --color-mocha: #6F4E37;
      --color-light-grey: #F8F8F8;
      --color-dark-grey: #333333;
      --font-heading: 'Cormorant Garamond', serif;
      --font-body: 'Montserrat', sans-serif;
    }

    body {
      margin: 0;
      font-family: var(--font-body);
      background-color: var(--color-white);
      color: var(--color-dark-grey);
      line-height: 1.7;
      -webkit-font-smoothing: antialiased;
      -moz-osx-font-smoothing: grayscale;
    }

    .app-container {
      display: flex;
      height: 100vh;
      overflow: hidden;
    }

    /* --- Left Column: Image Slideshow --- */
    .image-column {
      width: 50%;
      height: 100vh;
      position: relative;
      overflow: hidden;
      background-color: var(--color-black);
    }

    .slideshow-image {
      position: absolute;
      top: 0;
      left: 0;
      width: 100%;
      height: 100%;
      background-size: cover;
      background-position: center;
      opacity: 0;
      transition: opacity 1.5s ease-in-out;
      filter: brightness(0.8);
      z-index: 1;
    }

    .slideshow-image.active {
      opacity: 1;
      z-index: 2;
    }

    /* --- Right Column: Content --- */
    .content-column {
      width: 50%;
      height: 100vh;
      overflow-y: auto;
      scroll-behavior: smooth;
    }

    .content-wrapper {
      max-width: 700px;
      margin: 0 auto;
      padding: 4rem 3rem;
    }

    .main-heading {
        text-align: center;
        margin-bottom: 3rem;
    }

    .main-heading h1 {
        font-family: var(--font-heading);
        font-size: 3.5rem;
        font-weight: 600;
        margin: 0;
        letter-spacing: 2px;
    }

    .main-heading p {
        font-size: 1.2rem;
        margin-top: 0.5rem;
        color: var(--color-mocha);
    }

    .content-section {
      padding: 2.5rem 0;
      border-bottom: 1px solid #eee;
    }

    .content-section:last-of-type {
        border-bottom: none;
    }

    .content-section h2 {
      font-family: var(--font-heading);
      font-size: 2.5rem;
      font-weight: 500;
      margin-top: 0;
      margin-bottom: 1.5rem;
      color: var(--color-black);
    }

    .content-section p {
        font-size: 1.1rem;
        margin-bottom: 1rem;
    }

    .content-section a {
      color: var(--color-mocha);
      text-decoration: none;
      transition: color 0.3s ease;
      font-weight: 500;
    }

    .content-section a:hover {
      color: var(--color-black);
      text-decoration: underline;
    }

    /* --- Navigation Menu --- */
    .menu-button {
      position: fixed;
      top: 25px;
      left: 25px;
      z-index: 1001;
      background-color: rgba(0, 0, 0, 0.5);
      color: var(--color-white);
      border: 1px solid rgba(255, 255, 255, 0.5);
      padding: 0.5rem 1rem;
      font-size: 1rem;
      font-family: var(--font-body);
      cursor: pointer;
      border-radius: 5px;
      transition: background-color 0.3s ease;
    }

    .menu-button:hover {
      background-color: rgba(0, 0, 0, 0.8);
    }

    .nav-sidebar {
      position: fixed;
      top: 0;
      left: -300px;
      width: 280px;
      height: 100%;
      background-color: var(--color-white);
      box-shadow: 2px 0 15px rgba(0,0,0,0.1);
      transition: left 0.4s ease-in-out;
      z-index: 1000;
      padding-top: 80px;
    }

    .nav-sidebar.open {
      left: 0;
    }

    .nav-sidebar ul {
      list-style: none;
      padding: 0;
      margin: 0;
    }

    .nav-sidebar li a {
      display: block;
      padding: 1rem 2rem;
      color: var(--color-dark-grey);
      text-decoration: none;
      font-size: 1.2rem;
      font-family: var(--font-heading);
      transition: background-color 0.3s, color 0.3s;
    }

    .nav-sidebar li a:hover {
      background-color: var(--color-light-grey);
      color: var(--color-black);
    }

    .nav-overlay {
        position: fixed;
        top: 0;
        left: 0;
        width: 100%;
        height: 100%;
        background-color: rgba(0,0,0,0.4);
        opacity: 0;
        visibility: hidden;
        transition: opacity 0.4s ease-in-out, visibility 0.4s;
        z-index: 999;
    }

    .nav-overlay.open {
        opacity: 1;
        visibility: visible;
    }

    /* --- UI Elements --- */
    .rsvp-form input {
      width: 100%;
      max-width: 400px;
      padding: 0.8rem 1rem;
      margin-bottom: 1rem;
      border: 1px solid #ccc;
      border-radius: 5px;
      font-size: 1rem;
      font-family: var(--font-body);
      box-sizing: border-box;
    }

    .rsvp-form input:focus {
      outline: none;
      border-color: var(--color-mocha);
      box-shadow: 0 0 5px rgba(111, 78, 55, 0.3);
    }

    .button {
      background-color: var(--color-mocha);
      color: var(--color-white);
      border: none;
      padding: 0.8rem 2rem;
      font-size: 1.1rem;
      font-family: var(--font-body);
      cursor: pointer;
      border-radius: 5px;
      transition: background-color 0.3s ease, transform 0.2s ease;
      margin-top: 1rem;
      display: inline-block;
      text-align: center;
    }

    .button:hover {
      background-color: var(--color-black);
      transform: translateY(-2px);
    }

    .map-container, .spotify-container {
        overflow: hidden;
        border-radius: 8px;
        margin-top: 1.5rem;
    }

    .modal {
      position: fixed; 
      top: 0; 
      left: 0; 
      right: 0; 
      bottom: 0;
      background-color: rgba(0,0,0,0.5); 
      display: flex;
      align-items: center; 
      justify-content: center; 
      z-index: 2000;
      opacity: 0;
      visibility: hidden;
      transition: opacity 0.3s ease, visibility 0.3s;
    }

    .modal.show {
      opacity: 1;
      visibility: visible;
    }

    .modal-content {
      background-color: white; 
      padding: 2rem 3rem; 
      border-radius: 8px;
      text-align: center; 
      box-shadow: 0 5px 15px rgba(0,0,0,0.3);
      transform: translateY(-20px);
      transition: transform 0.3s ease;
    }

    .modal.show .modal-content {
        transform: translateY(0);
    }

    .modal-content p {
      font-family: var(--font-body); 
      font-size: 1.1rem; 
      margin: 0 0 1.5rem 0;
    }

    /* --- Responsive Design --- */
    @media (max-width: 1024px) {
      .app-container {
        flex-direction: column;
      }
      .image-column {
        display: none; /* Hide image column on smaller screens */
      }
      .content-column {
        width: 100%;
      }
      .content-wrapper {
        padding: 3rem 1.5rem;
      }
      .menu-button {
        background-color: var(--color-mocha);
        border-color: var(--color-mocha);
      }
    }

    @media (max-width: 768px) {
        .main-heading h1 {
            font-size: 2.5rem;
        }
        .main-heading p {
            font-size: 1rem;
        }
        .content-section h2 {
            font-size: 2rem;
        }
    }
  `}</style>
);

// --- Modal Component ---
const Modal = ({ message, onClose }) => {
    if (!message) return null;
    return (
      <div className={`modal ${message ? 'show' : ''}`} onClick={onClose}>
        <div className="modal-content" onClick={e => e.stopPropagation()}>
          <p>{message}</p>
          <button className="button" onClick={onClose}>Close</button>
        </div>
      </div>
    );
};

// --- ImageSlideshow Component ---
const ImageSlideshow = () => {
  // Images of Coles Garden, Oklahoma City
  const images = [
    'https://images.unsplash.com/photo-1596328330594-215511e03254?q=80&w=2070&auto=format&fit=crop',
    'https://images.unsplash.com/photo-1587300003412-e0265a1d5a7a?q=80&w=1974&auto=format&fit=crop',
    'https://images.unsplash.com/photo-1595878831649-5c065f3c02e5?q=80&w=1974&auto=format&fit=crop',
    'https://images.unsplash.com/photo-1618763299035-943c2aed26c6?q=80&w=1974&auto=format&fit=crop',
    'https://images.unsplash.com/photo-1621204101124-af89f2b2a64e?q=80&w=1964&auto=format&fit=crop',
  ];

  const [currentIndex, setCurrentIndex] = useState(0);
  const [isLoaded, setIsLoaded] = useState(false);

  useEffect(() => {
    // This ensures the transition happens after the initial render.
    const timer = setTimeout(() => setIsLoaded(true), 10);

    const interval = setInterval(() => {
      setCurrentIndex((prevIndex) => (prevIndex + 1) % images.length);
    }, 5000); // Change image every 5 seconds

    return () => {
        clearTimeout(timer);
        clearInterval(interval);
    };
  }, [images.length]);

  return (
    <div className="image-column">
      {images.map((image, index) => (
        <div
          key={index}
          className={`slideshow-image ${index === currentIndex && isLoaded ? 'active' : ''}`}
          style={{ backgroundImage: `url(${image})` }}
          aria-label="Slideshow image of Coles Garden"
        ></div>
      ))}
    </div>
  );
};

// --- Navigation Component ---
const Navigation = ({ onNavigate, isNavOpen, setIsNavOpen }) => {
  const sections = [
    { id: 'schedule', title: 'Schedule' },
    { id: 'menu', title: 'Menu' },
    { id: 'hotels', title: 'Hotels' },
    { id: 'rsvp', title: 'RSVP' },
    { id: 'photos', title: 'Photos' },
    { id: 'games', title: 'Games' },
    { id: 'spotify', title: 'Playlist' },
    { id: 'registry', title: 'Registry' },
    { id: 'directions', title: 'Directions' },
  ];

  const handleNavClick = (id) => {
    onNavigate(id);
    setIsNavOpen(false);
  };

  return (
    <>
      <button className="menu-button" onClick={() => setIsNavOpen(!isNavOpen)}>
        MENU
      </button>
      <div className={`nav-overlay ${isNavOpen ? 'open' : ''}`} onClick={() => setIsNavOpen(false)}></div>
      <nav className={`nav-sidebar ${isNavOpen ? 'open' : ''}`}>
        <ul>
          {sections.map((section) => (
            <li key={section.id}>
              <a href={`#${section.id}`} onClick={(e) => { e.preventDefault(); handleNavClick(section.id); }}>
                {section.title}
              </a>
            </li>
          ))}
        </ul>
      </nav>
    </>
  );
};

// --- Main App Component ---
function App() {
  const [rsvpName, setRsvpName] = useState("");
  const [isNavOpen, setIsNavOpen] = useState(false);
  const [modalMessage, setModalMessage] = useState('');
  const contentColumnRef = useRef(null);

  // Create a ref for each section
  const sectionRefs = {
    schedule: useRef(null),
    menu: useRef(null),
    hotels: useRef(null),
    rsvp: useRef(null),
    photos: useRef(null),
    games: useRef(null),
    spotify: useRef(null),
    registry: useRef(null),
    directions: useRef(null),
  };

  const handleNavigate = (id) => {
    sectionRefs[id].current.scrollIntoView({
      behavior: 'smooth',
      block: 'start'
    });
  };

  const handleRsvpSubmit = () => {
    if (rsvpName.trim()) {
        setModalMessage(`Thank you for your RSVP, ${rsvpName}!`);
        setRsvpName('');
    } else {
        setModalMessage('Please enter your name to RSVP.');
    }
  };

  return (
    <>
      <GlobalStyles />
      <Modal message={modalMessage} onClose={() => setModalMessage('')} />
      <div className="app-container">
        <Navigation onNavigate={handleNavigate} isNavOpen={isNavOpen} setIsNavOpen={setIsNavOpen} />
        <ImageSlideshow />
        <main className="content-column" ref={contentColumnRef}>
          <div className="content-wrapper">
            <header className="main-heading">
              <h1>The Bowens</h1>
              <p>July 17, 2027 — Coles Garden, Oklahoma City</p>
            </header>

            <section id="schedule" ref={sectionRefs.schedule} className="content-section">
              <h2>Schedule</h2>
              <p><strong>Ceremony:</strong> 4:30 PM</p>
              <p><strong>Reception:</strong> 6:00 PM</p>
              <p>Join us for an evening of celebration, dinner, and dancing following the ceremony.</p>
            </section>

            <section id="menu" ref={sectionRefs.menu} className="content-section">
              <h2>Menu</h2>
              <p><strong>Appetizer:</strong> Bruschetta with Balsamic Glaze</p>
              <p><strong>Entree:</strong> Herb-Crusted Chicken Breast with Garlic Mashed Potatoes</p>
              <p><strong>Sides:</strong> Seasonal Roasted Vegetables & Artisan Rolls</p>
            </section>

            <section id="hotels" ref={sectionRefs.hotels} className="content-section">
              <h2>Hotel Recommendations</h2>
              <p><a href="#" target="_blank" rel="noopener noreferrer">Omni Oklahoma City Hotel</a> — ⭐ 4.5 — ~$200/night</p>
              <p><a href="#" target="_blank" rel="noopener noreferrer">Colcord Hotel</a> — ⭐ 4.6 — ~$180/night</p>
              <p><a href="#" target="_blank" rel="noopener noreferrer">Skirvin Hilton Oklahoma City</a> — ⭐ 4.3 — ~$160/night</p>
            </section>

            <section id="rsvp" ref={sectionRefs.rsvp} className="content-section">
              <h2>RSVP</h2>
              <p>Please RSVP by June 17, 2027</p>
              <div className="rsvp-form">
                <input
                  type="text"
                  placeholder="Your Full Name"
                  value={rsvpName}
                  onChange={(e) => setRsvpName(e.target.value)}
                />
                <button className="button" onClick={handleRsvpSubmit}>Submit RSVP</button>
              </div>
            </section>

            <section id="photos" ref={sectionRefs.photos} className="content-section">
              <h2>Photo Upload</h2>
              <p>Scan the QR codes at your table or click below to upload your photos from our special day!</p>
              <button className="button">Upload Photos</button>
            </section>

            <section id="games" ref={sectionRefs.games} className="content-section">
              <h2>Games</h2>
              <p>Bingo & an Over/Under Prop Bet will be available at the reception for some friendly fun!</p>
