// src/components/ImageSlideshow/ImageSlideshow.js
import React, { useState, useEffect } from 'react';

// --- IMPORT YOUR VENUE IMAGES ---
// UPDATED PATHS: Images are now in public/assets/images and accessed via %PUBLIC_URL%
// When importing images from the public folder in React components,
// you don't import them like JS modules. Instead, you reference them
// directly using process.env.PUBLIC_URL
// So, we will not use 'import' statements for these images anymore in this component.
// Instead, we will directly use their paths in the 'images' array.

const ImageSlideshow = () => {
  // Directly reference images in the public folder
  // Make sure these files exist in your public/assets/images/ directory
  const images = [
    `${process.env.PUBLIC_URL}/assets/images/WEDDINGS-THE-ROYAL-GARDEN-10.webp`,
    `${process.env.PUBLIC_URL}/assets/images/WEDDINGS-Waterfall-02.webp`,
    `${process.env.PUBLIC_URL}/assets/images/WEDDINGS-Waterfall-03.webp`,
    `${process.env.PUBLIC_URL}/assets/images/WEDDINGS-Waterfall-06.webp`
  ];
  const [currentIndex, setCurrentIndex] = useState(0);
  const [isLoaded, setIsLoaded] = useState(false);

  useEffect(() => {
    const timer = setTimeout(() => setIsLoaded(true), 10);
    const interval = setInterval(() => {
      setCurrentIndex((prevIndex) => (prevIndex + 1) % images.length);
    }, 5000);
    return () => { clearTimeout(timer); clearInterval(interval); };
  }, [images.length]);

  return (
    <div className="image-column">
      {images.map((image, index) => (
        <div
          key={index}
          className={`slideshow-image ${index === currentIndex && isLoaded ? 'active' : ''}`}
          style={{ backgroundImage: `url(${image})` }}
          aria-label="Slideshow image of Coles Garden"
        ></div>
      ))}
      <div className="slideshow-overlay">
      </div>
    </div>
  );
};

export default ImageSlideshow;
