// src/modals/PhotoUploadModal/PhotoUploadModal.js
import React, { useState } from 'react';
import styles from './PhotoUploadModal.module.css';

// A helper function to format file size
const formatBytes = (bytes, decimals = 1) => {
    if (bytes === 0) return '0 Bytes';
    const k = 1024;
    const dm = decimals < 0 ? 0 : decimals;
    const sizes = ['Bytes', 'KB', 'MB', 'GB'];
    const i = Math.floor(Math.log(bytes) / Math.log(k));
    return parseFloat((bytes / Math.pow(k, i)).toFixed(dm)) + ' ' + sizes[i];
};

const PhotoUploadModal = ({ isOpen, onClose, onUploadSuccess }) => {
    const [selectedFiles, setSelectedFiles] = useState([]);
    const [uploadStates, setUploadStates] = useState({});
    const [isUploadingAll, setIsUploadingAll] = useState(false);
    const [overallError, setOverallError] = useState('');
    const [isSubmitted, setIsSubmitted] = useState(false);

    const CLOUDINARY_CLOUD_NAME = 'dtmhmxqiw'; // Your Cloud Name
    const CLOUDINARY_UPLOAD_PRESET = 'wedding_upload_preset'; // Your Unsigned Upload Preset Name

    const addFiles = (newFiles) => {
        const validFiles = Array.from(newFiles).filter(file => file.type.startsWith('image/'));
        setSelectedFiles(prevFiles => {
            const updatedFiles = [...prevFiles];
            validFiles.forEach(newFile => {
                if (!updatedFiles.some(existingFile => existingFile.name === newFile.name && existingFile.size === newFile.size)) {
                    updatedFiles.push(newFile);
                }
            });
            return updatedFiles;
        });

        setUploadStates(prevStates => {
            const newUploadStates = { ...prevStates };
            validFiles.forEach(file => {
                if (!newUploadStates[file.name]) {
                    newUploadStates[file.name] = {
                        progress: 0,
                        error: '',
                        success: false,
                        url: '',
                        previewUrl: URL.createObjectURL(file)
                    };
                }
            });
            return newUploadStates;
        });
        setOverallError('');
        setIsSubmitted(false);
    };

    const handleFileChange = (event) => {
        if (event.target.files) addFiles(event.target.files);
        event.target.value = null;
    };

    const handleDrop = (event) => {
        event.preventDefault();
        event.currentTarget.classList.remove(styles['drag-over']);
        addFiles(event.dataTransfer.files);
    };

    const handleDragOver = (event) => {
        event.preventDefault();
        event.currentTarget.classList.add(styles['drag-over']);
    };

    const handleDragLeave = (event) => {
        event.currentTarget.classList.remove(styles['drag-over']);
    };

    const removeFile = (fileNameToRemove) => {
        setSelectedFiles(prev => {
            const updatedFiles = prev.filter(file => file.name !== fileNameToRemove);
            if (updatedFiles.length === 0 && uploadStates[fileNameToRemove]?.success) {
                setIsSubmitted(false);
            }
            return updatedFiles;
        });
        setUploadStates(prev => {
            const newStates = { ...prev };
            delete newStates[fileNameToRemove];
            return newStates;
        });
        setOverallError('');
    };

    const handleUploadAll = async () => {
        if (selectedFiles.length === 0) {
            setOverallError('Please select at least one file to upload.');
            return;
        }

        setIsUploadingAll(true);
        setOverallError('');
        const uploadedUrls = [];
        let hasError = false;

        for (const file of selectedFiles) {
            if (uploadStates[file.name]?.success) {
                uploadedUrls.push(uploadStates[file.name].url);
                continue;
            }

            setUploadStates(prev => ({
                ...prev,
                [file.name]: { ...prev[file.name], progress: 1, error: '', success: false }
            }));

            const reader = new FileReader();
            reader.readAsDataURL(file);

            await new Promise(resolve => {
                reader.onloadend = async () => {
                    const base64data = reader.result;

                    try {
                        const response = await fetch('/.netlify/functions/upload-image', {
                            method: 'POST',
                            headers: { 'Content-Type': 'application/json' },
                            body: JSON.stringify({ file: base64data }),
                        });

                        if (!response.ok) {
                            const errorData = await response.json();
                            throw new Error(errorData.message || 'Upload failed.');
                        }

                        const data = await response.json();
                        uploadedUrls.push(data.secure_url);
                        setUploadStates(prev => ({ ...prev, [file.name]: { ...prev[file.name], progress: 100, success: true, url: data.secure_url } }));

                    } catch (error) {
                        console.error(`Error uploading ${file.name}:`, error);
                        setUploadStates(prev => ({ ...prev, [file.name]: { ...prev[file.name], error: error.message || 'Failed', success: false } }));
                        hasError = true;
                    } finally {
                        resolve();
                    }
                };
                reader.onerror = () => {
                    setUploadStates(prev => ({ ...prev, [file.name]: { ...prev[file.name], error: 'Failed to read file.', success: false } }));
                    hasError = true;
                    resolve();
                };
            });
        }

        setIsUploadingAll(false);
        if (!hasError) {
            onUploadSuccess(uploadedUrls);
            setIsSubmitted(true);
        } else {
            setOverallError('Some files failed to upload. Please check errors above.');
        }
    };

    const handleCloseModal = () => {
        setSelectedFiles([]);
        setUploadStates({});
        setIsSubmitted(false);
        setOverallError('');
        onClose();
    };

    if (!isOpen) return null;

    const hasFilesSelected = selectedFiles.length > 0;

    return (
        <div className={`simple-modal ${isOpen ? 'show' : ''}`} onClick={handleCloseModal} role="dialog" aria-modal="true">
            <div className={`simple-modal-content ${styles['photo-upload-modal-content']}`} onClick={e => e.stopPropagation()}>
                <button className={styles['close-button']} onClick={handleCloseModal} disabled={isUploadingAll} aria-label="Close modal">
                    <svg xmlns="http://www.w3.org/2000/svg" viewBox="0 0 24 24" fill="currentColor">
                        <path fillRule="evenodd" d="M5.47 5.47a.75.75 0 011.06 0L12 10.94l5.47-5.47a.75.75 0 111.06 1.06L13.06 12l5.47 5.47a.75.75 0 11-1.06 1.06L12 13.06l-5.47 5.47a.75.75 0 01-1.06-1.06L10.94 12 5.47 6.53a.75.75 0 010-1.06z" clipRule="evenodd" />
                    </svg>
                </button>

                <h2 className={styles['modal-title']}>Upload and attach files</h2>
                <p className={styles['modal-subtitle']}>Upload images to the project gallery.</p>

                {/* Always visible Upload Box / Drag-and-Drop Area */}
                <div
                    className={`${styles['upload-box']} ${styles['drag-drop-box']} ${overallError ? styles['has-error'] : ''}`}
                    onDrop={handleDrop}
                    onDragOver={handleDragOver}
                    onDragLeave={handleDragLeave}
                >
                    <img src={`${process.env.PUBLIC_URL}/assets/images/cloud-computing.png`} alt="Upload Cloud Icon" className={styles['cloud-upload-icon']} />
                    {/* Combined text for "Click to upload or drag and drop" */}
                    <p className={styles['upload-text-container']}>
                        <label htmlFor="photo-upload-input" className={styles['upload-clickable-text']}>
                            Click to upload
                        </label>
                        <span className={styles['upload-static-text']}> or drag and drop</span>
                    </p>
                    <span className={styles['upload-hint']}>SVG, PNG, JPG or GIF (max. 800x400px)</span>
                    <input
                        type="file"
                        accept="image/svg+xml,image/png,image/jpeg,image/gif"
                        onChange={handleFileChange}
                        className={styles['file-input']}
                        id="photo-upload-input"
                        multiple // Allow multiple file selection
                    />
                </div>

                {/* Display File List if files are selected */}
                {hasFilesSelected && (
                    <div className={styles['file-list-container']}>
                        {selectedFiles.map((file) => {
                            const state = uploadStates[file.name] || {};
                            const isUploadingThisFile = isUploadingAll && !state.success && !state.error; // Track individual file upload state

                            return (
                                <div key={file.name} className={styles['file-preview-item']}>
                                    {/* No file-icon-placeholder, use a simple SVG link icon */}
                                    <svg className={styles['file-icon']} xmlns="http://www.w3.org/2000/svg" fill="none" viewBox="0 0 24 24" strokeWidth={1.5} stroke="currentColor">
                                        <path strokeLinecap="round" strokeLinejoin="round" d="M18.375 12.739l-7.693 7.693a4.5 4.5 0 01-6.364-6.364l10.94-10.94A3 3 0 1119.5 7.372L8.552 18.32m.009-.01l-.01.01m5.699-9.941l-7.81 7.81a1.5 1.5 0 002.122 2.122l7.81-7.81" />
                                    </svg>
                                    <div className={styles['file-details']}>
                                        <p className={styles['file-name']}>{file.name}</p>
                                        <span className={styles['file-size']}>{formatBytes(file.size)}</span> {/* Use formatBytes here */}
                                    </div>

                                    <div className={styles['file-status-area']}> {/* Container for status elements */}
                                        {isUploadingThisFile && (
                                            <>
                                                <span className={styles['upload-progress-text']}>{state.progress || 0}%</span> {/* Ensure 0% is shown */}
                                                <div className={styles['progress-spinner']}></div> {/* Spinner */}
                                            </>
                                        )}
                                        {state.success && <span className={styles['upload-status']}><svg viewBox="0 0 24 24" fill="currentColor" className={styles['success-icon']}><path fillRule="evenodd" d="M19.916 4.626a.75.75 0 01.208 1.04l-9 13.5a.75.75 0 01-1.154.114l-3-3a.75.75 0 011.06-1.06l2.769 2.769 8.792-13.188a.75.75 0 011.04-.208z" clipRule="evenodd" /></svg></span>}
                                        {state.error && <span className={styles['error-message-inline']}>Error</span>} {/* Inline error */}
                                    </div>

                                    {!isUploadingAll && ( // Only show X if not currently uploading overall
                                        <button className={styles['remove-file-button']} onClick={() => removeFile(file.name)} aria-label={`Remove ${file.name}`}>
                                            <svg xmlns="http://www.w3.org/2000/svg" viewBox="0 0 16 16" fill="currentColor"><path d="M5.28 4.22a.75.75 0 0 0-1.06 1.06L6.94 8l-2.72 2.72a.75.75 0 1 0 1.06 1.06L8 9.06l2.72 2.72a.75.75 0 1 0 1.06-1.06L9.06 8l2.72-2.72a.75.75 0 0 0-1.06-1.06L8 6.94 5.28 4.22Z" /></svg>
                                        </button>
                                    )}
                                </div>
                            );
                        })}
                    </div>
                )}

                {overallError && <p className={styles['overall-error-message']}>{overallError}</p>}

                {/* Main Action Buttons */}
                <div className={styles['button-row']}>
                    {isSubmitted ? (
                        <p className={styles['submission-success-message']}>
                            All photos uploaded to the <a href="#" onClick={handleCloseModal} className={styles['gallery-link']}>gallery</a>!
                        </p>
                    ) : (
                        <button
                            className={`${styles.button} ${styles.primary}`}
                            onClick={handleUploadAll}
                            disabled={selectedFiles.length === 0 || isUploadingAll}
                        >
                            {isUploadingAll ? 'Uploading...' : 'Submit'}
                        </button>
                    )}
                </div>
            </div>
        </div>
    );
};

export default PhotoUploadModal;
