// src/modals/PhotoUploadModal/PhotoUploadModal.js
import React, { useState } from 'react';
import styles from './PhotoUploadModal.module.css';

const PhotoUploadModal = ({ isOpen, onClose, onUploadSuccess }) => {
    const [selectedFiles, setSelectedFiles] = useState([]);
    const [uploadStates, setUploadStates] = useState({});
    const [isUploadingAll, setIsUploadingAll] = useState(false);
    const [overallError, setOverallError] = useState('');
    const [isSubmitted, setIsSubmitted] = useState(false);

    // BEST PRACTICE: Store credentials in environment variables
    // In a .env file: REACT_APP_CLOUDINARY_CLOUD_NAME='your_cloud_name'
    // const CLOUDINARY_CLOUD_NAME = process.env.REACT_APP_CLOUDINARY_CLOUD_NAME;
    // const CLOUDINARY_UPLOAD_PRESET = process.env.REACT_APP_CLOUDINARY_UPLOAD_PRESET;

    const addFiles = (newFiles) => {
        const validFiles = Array.from(newFiles).filter(file => file.type.startsWith('image/'));
        const newUploadStates = {};

        const updatedFiles = [...selectedFiles];
        validFiles.forEach(file => {
            // Avoid adding duplicate files
            if (!updatedFiles.some(existingFile => existingFile.name === file.name && existingFile.size === file.size)) {
                updatedFiles.push(file);
                newUploadStates[file.name] = {
                    progress: 0,
                    error: '',
                    success: false,
                    url: '',
                    previewUrl: URL.createObjectURL(file)
                };
            }
        });

        setSelectedFiles(updatedFiles);
        setUploadStates(prevStates => ({ ...prevStates, ...newUploadStates }));
        setOverallError('');
        setIsSubmitted(false);
    };

    const handleFileChange = (event) => {
        if (event.target.files) {
            addFiles(event.target.files);
        }
        event.target.value = null; // Allows selecting the same file again
    };

    const handleDrop = (event) => {
        event.preventDefault();
        event.currentTarget.classList.remove(styles['drag-over']);
        addFiles(event.dataTransfer.files);
    };

    const handleDragOver = (event) => {
        event.preventDefault();
        event.currentTarget.classList.add(styles['drag-over']);
    };

    const handleDragLeave = (event) => {
        event.currentTarget.classList.remove(styles['drag-over']);
    };

    const removeFile = (fileNameToRemove) => {
        // Revoke the object URL to prevent memory leaks
        const fileState = uploadStates[fileNameToRemove];
        if (fileState && fileState.previewUrl) {
            URL.revokeObjectURL(fileState.previewUrl);
        }

        setSelectedFiles(prev => prev.filter(file => file.name !== fileNameToRemove));
        setUploadStates(prev => {
            const newStates = { ...prev };
            delete newStates[fileNameToRemove];
            return newStates;
        });
        setOverallError('');
    };

    const handleUploadAll = async () => {
        if (selectedFiles.length === 0) {
            setOverallError('Please select at least one file to upload.');
            return;
        }

        setIsUploadingAll(true);
        setOverallError('');
        const uploadedUrls = [];
        let hasError = false;

        const uploadPromises = selectedFiles.map(file => {
            // Skip already uploaded files
            if (uploadStates[file.name]?.success) {
                uploadedUrls.push(uploadStates[file.name].url);
                return Promise.resolve();
            }

            // Set initial uploading state
            setUploadStates(prev => ({
                ...prev,
                [file.name]: { ...prev[file.name], progress: 1, error: '', success: false }
            }));

            return new Promise((resolve) => {
                const reader = new FileReader();
                reader.readAsDataURL(file);
                reader.onloadend = async () => {
                    try {
                        const response = await fetch('/.netlify/functions/upload-image', {
                            method: 'POST',
                            headers: { 'Content-Type': 'application/json' },
                            body: JSON.stringify({ file: reader.result }),
                        });

                        if (!response.ok) {
                            const errorData = await response.json();
                            throw new Error(errorData.message || 'Upload failed.');
                        }

                        const data = await response.json();
                        uploadedUrls.push(data.secure_url);
                        setUploadStates(prev => ({ ...prev, [file.name]: { ...prev[file.name], progress: 100, success: true, url: data.secure_url } }));
                    } catch (error) {
                        console.error(`Error uploading ${file.name}:`, error);
                        setUploadStates(prev => ({ ...prev, [file.name]: { ...prev[file.name], error: error.message || 'Upload failed', success: false } }));
                        hasError = true;
                    } finally {
                        resolve();
                    }
                };
                reader.onerror = () => {
                    setUploadStates(prev => ({ ...prev, [file.name]: { ...prev[file.name], error: 'Failed to read file.', success: false } }));
                    hasError = true;
                    resolve();
                };
            });
        });

        await Promise.all(uploadPromises);

        setIsUploadingAll(false);
        if (!hasError) {
            onUploadSuccess(uploadedUrls);
            setIsSubmitted(true);
        } else {
            setOverallError('Some files failed to upload. Please review the errors and try again.');
        }
    };

    const handleCloseModal = () => {
        // Clean up object URLs on close
        Object.values(uploadStates).forEach(state => {
            if (state.previewUrl) URL.revokeObjectURL(state.previewUrl);
        });
        setSelectedFiles([]);
        setUploadStates({});
        setIsSubmitted(false);
        setOverallError('');
        onClose();
    };

    if (!isOpen) return null;

    const hasFilesSelected = selectedFiles.length > 0;

    return (
        <div className={`simple-modal ${isOpen ? 'show' : ''}`} onClick={handleCloseModal} role="dialog" aria-modal="true">
            <div className={`simple-modal-content ${styles['photo-upload-modal-content']}`} onClick={e => e.stopPropagation()}>
                <button className={styles['close-button']} onClick={handleCloseModal} disabled={isUploadingAll} aria-label="Close modal">
                    <svg xmlns="http://www.w3.org/2000/svg" viewBox="0 0 24 24" fill="currentColor"><path fillRule="evenodd" d="M5.47 5.47a.75.75 0 011.06 0L12 10.94l5.47-5.47a.75.75 0 111.06 1.06L13.06 12l5.47 5.47a.75.75 0 11-1.06 1.06L12 13.06l-5.47 5.47a.75.75 0 01-1.06-1.06L10.94 12 5.47 6.53a.75.75 0 010-1.06z" clipRule="evenodd" /></svg>
                </button>

                <h2 className={styles['modal-title']}>Upload and attach files</h2>
                <p className={styles['modal-subtitle']}>Upload images to the project gallery.</p>

                <div
                    className={`${styles['upload-box']} ${overallError ? styles['has-error'] : ''}`}
                    onDrop={handleDrop}
                    onDragOver={handleDragOver}
                    onDragLeave={handleDragLeave}
                >
                    <input
                        type="file"
                        accept="image/svg+xml,image/png,image/jpeg,image/gif"
                        onChange={handleFileChange}
                        className={styles['file-input']}
                        id="photo-upload-input"
                        multiple
                        disabled={isUploadingAll}
                    />
                    <img src={`${process.env.PUBLIC_URL}/assets/images/cloud-computing.png`} alt="Upload" className={styles['cloud-upload-icon']} />
                    <p>
                        <label htmlFor="photo-upload-input" className={styles['upload-clickable-text']}>
                            Click to upload
                        </label>
                        <span className={styles['upload-static-text']}>or drag and drop</span>
                    </p>
                    <span className={styles['upload-hint']}>SVG, PNG, JPG or GIF</span>
                </div>

                {hasFilesSelected && (
                    <div className={styles['file-list-container']}>
                        {selectedFiles.map((file) => {
                            const state = uploadStates[file.name] || {};
                            return (
                                <div key={file.name} className={styles['file-preview-item']}>
                                    <div className={styles['file-thumbnail']}>
                                        {state.previewUrl && <img src={state.previewUrl} alt="Preview" className={styles['thumbnail-image']} />}
                                    </div>
                                    <div className={styles['file-details']}>
                                        <p className={styles['file-name']}>{file.name}</p>
                                        <p className={styles['file-size']}>{Math.round(file.size / 1024)} KB</p>

                                        {isUploadingAll && !state.error && !state.success && (
                                            <div className={styles['progress-wrapper']}>
                                                <div className={styles['progress-bar']} style={{ width: `${state.progress}%` }}></div>
                                            </div>
                                        )}
                                        {state.success && <span className={styles['upload-status']}><svg viewBox="0 0 24 24" fill="currentColor" className={styles['success-icon']}><path fillRule="evenodd" d="M19.916 4.626a.75.75 0 01.208 1.04l-9 13.5a.75.75 0 01-1.154.114l-3-3a.75.75 0 011.06-1.06l2.769 2.769 8.792-13.188a.75.75 0 011.04-.208z" clipRule="evenodd" /></svg>Uploaded</span>}
                                        {state.error && <p className={styles['error-message']}>{state.error}</p>}
                                    </div>
                                    {!isUploadingAll && (
                                        <button className={styles['remove-file-button']} onClick={() => removeFile(file.name)} aria-label={`Remove ${file.name}`}>
                                            <svg xmlns="http://www.w3.org/2000/svg" viewBox="0 0 24 24" fill="currentColor"><path fillRule="evenodd" d="M16.5 4.478l.271.272a.75.75 0 010 1.06L13.06 9.75l3.71 3.71a.75.75 0 01-1.06 1.06l-3.71-3.71-3.71 3.71a.75.75 0 01-1.06-1.06l3.71-3.71-3.71-3.71a.75.75 0 111.06-1.06l3.71 3.71l3.71-3.71a.75.75 0 011.06 0z" clipRule="evenodd" /></svg>
                                        </button>
                                    )}
                                </div>
                            );
                        })}
                    </div>
                )}

                {overallError && <p className={styles['overall-error-message']}>{overallError}</p>}

                <div className={styles['button-row']}>
                    {isSubmitted ? (
                        <p className={styles['submission-success-message']}>
                            All photos uploaded to the <button onClick={handleCloseModal} className={styles['gallery-link']}>gallery</button>!
                        </p>
                    ) : (
                        <button
                            className={styles['upload-button']}
                            onClick={handleUploadAll}
                            disabled={!hasFilesSelected || isUploadingAll}
                        >
                            {isUploadingAll ? 'Uploading...' : `Submit ${selectedFiles.length} File(s)`}
                        </button>
                    )}
                </div>
            </div>
        </div>
    );
};

export default PhotoUploadModal;
