import React, { useState, useEffect } from 'react';
import styles from './ImageSlideshow.module.css'; // This will also need to be moved

const ImageSlideshow = () => {
  const images = [
    `/assets/images/WEDDINGS-THE-ROYAL-GARDEN-10.webp`, // Updated path for src/assets
    `/assets/images/WEDDINGS-Waterfall-02.webp`,
    `/assets/images/WEDDINGS-Waterfall-03.webp`,
    `/assets/images/WEDDINGS-Waterfall-06.webp`
  ];
  const [currentIndex, setCurrentIndex] = useState(0);
  const [isLoaded, setIsLoaded] = useState(false);

  useEffect(() => {
    const timer = setTimeout(() => setIsLoaded(true), 10);
    const interval = setInterval(() => {
      setCurrentIndex((prevIndex) => (prevIndex + 1) % images.length);
    }, 5000);
    return () => { clearTimeout(timer); clearInterval(interval); };
  }, [images.length]);

  return (
    <div className="image-column">
      {images.map((image, index) => (
        <div
          key={index}
          className={`slideshow-image ${index === currentIndex && isLoaded ? 'active' : ''}`}
          style={{ backgroundImage: `url(${image})` }}
          aria-label="Slideshow image of Coles Garden"
        ></div>
      ))}
      <div className={styles['monogram-overlay']}>
        <div className={styles['monogram-text']}>T | B</div>
      </div>
    </div>
  );
};

export default ImageSlideshow;
